pragma solidity^0.5.0;

contract CoShoe {

    // variable initialisation
    uint256 price;
    uint256 shoesSold;
    uint256 count;
    uint256 totalSupply;

    // creates the representational stuct of shoe (the token)
    struct Shoe {
        address payable owner; //address of the owner of the shoe token
        string name; //name of owner of shoe
        string image; //image url
        bool sold;
    }

    // array to hold the shoes
    Shoe[] public shoes;

    // event declarations
    event Purchase(
      address indexed _buyer,
      uint256 _value
    );

    //creates constructor for 100 tokens, count is increased each time in case needs to be verified that count equals 100
    constructor(uint256 _initialSupply, uint256 _shoesSold) public {
        // set constructor arguments
        totalSupply = _initialSupply;
        price = 5*10**17; // 0.5 ETH converted to wei
        shoesSold = _shoesSold;

        for (uint i = 1; i <= totalSupply; i++) {
        shoes.push(Shoe(msg.sender, "","",false));
        }
    }


    //function for purchasing shoe tokens
    function purchase(string memory _name, string memory _image) public payable returns(uint){

        // validation checks
        //require that the number of shoe tokens to be purchased are less than the number of shoe tokens minted
        require(shoesSold < shoes.length, "No more remaining tokens. Please specify a number less than the remaining amount");
        require(msg.value == price, "The value of the purchase does not equal the price. Please specify the correct price (0.5 ETH)");

        //sell the next shoe token
        uint256 i = shoesSold + 1;

        //ensure that the token has not already been purchased
        require(shoes[i].sold == false,'This token has already been purchased.');

        // update the values of the next shoe
        // set the owner of the token to the caller of the function
        shoes[i].owner = msg.sender;
        // register the token to the owners identifer
        shoes[i].name = _name;
        // set the token image to the supplied image url
        shoes[i].image = _image;
        // register the token as sold
        shoes[i].sold = true;
        // increment the number of tokens that have been sold
        shoesSold++;

        // emit the purchase event
        emit Purchase(msg.sender, msg.value);

        // finally return the number of shoes that have been sold
        return shoesSold;
    }

    // test functions

    function getNumberofTokens() public view returns(uint counts) {
        return shoes.length;
    }


    //function to return an array of bools of an array of all the purchases that are true
    function checkPurchases() external view returns (bool[] memory){
        bool[] memory checkPurchase;
        for (uint256 i = 0; i < shoes.length; i++){
            if (shoes[i].owner == msg.sender){
                checkPurchase[i] = true;
            }
        }
        return checkPurchase;
    }

}
